mapper.select:
SELECT id, source_wallet_id, target_wallet_id, amount
FROM transaction
WHERE source_wallet_id = #{sourceWalletId}

SELECT id, source_wallet_id, target_wallet_id, amount
FROM transaction
WHERE target_wallet_id = #{targetWalletId}

SELECT id, source_wallet_id, target_wallet_id, amount
FROM transaction
WHERE id = #{transactionId} AND source_wallet_id = #{sourceWalletId}

SELECT id, source_wallet_id, target_wallet_id, amount
FROM transaction
WHERE id = #{transactionId} AND target_wallet_id = #{targetWalletId}

SELECT COUNT(*) > 0
FROM transaction
WHERE id = #{transactionId} AND source_wallet_id = #{sourceWalletId}

SELECT COUNT(*) > 0
FROM transaction
WHERE id = #{transactionId} AND target_wallet_id = #{targetWalletId}
